import React, { useState, useEffect } from "react";

const API_URL = "http://0.0.0.0:7020";

export default function AddEmployee({ loadData }) {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch all employees (ignore year)
  const fetchEmployees = async () => {
    setLoading(true);
    try {
      const res = await fetch(`${API_URL}/api/employees`);
      const data = await res.json();
      setEmployees(data || []);
    } catch (err) {
      console.error("Error loading employees:", err);
      setEmployees([]);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  // Delete employee with confirmation
  const handleDelete = async (id, name) => {
    if (!window.confirm(`Θέλετε να διαγραφεί ο εργαζόμενος "${name}" ;`)) return;

    try {
      await fetch(`${API_URL}/api/delete-employee/${id}`, { method: "DELETE" });
      await fetchEmployees();
      if (loadData) loadData(); // refresh parent data if needed
    } catch (err) {
      console.error("Delete employee error:", err);
      alert("Σφάλμα κατά τη διαγραφή του εργαζόμενου!");
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-xl font-bold mb-4">Λίστα Όλων των Εργαζομένων</h2>

      {loading ? (
        <p>Φόρτωση...</p>
      ) : employees.length === 0 ? (
        <p>Δεν υπάρχουν εργαζόμενοι.</p>
      ) : (
        <table className="table-auto border-collapse border w-full">
          <thead>
            <tr>
              <th className="border p-2">Όνομα</th>
              <th className="border p-2">Ενέργειες</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((emp) => (
              <tr key={emp.id}>
                <td className="border p-2">{emp.name}</td>
                <td className="border p-2 text-center">
                  <button
                    onClick={() => handleDelete(emp.id, emp.name)}
                    className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600"
                  >
                    ❌
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
