import React, { useState } from "react";
import DatePicker from "react-multi-date-picker";

export default function EmployeeList({
  employees,
  leaves,
  year,
  setYear,
  addLeave,       // expects { employeeId, from, to, days }
  loadData,
  doExport,
  handleEditLeave,
  handleDeleteLeave,
  API_URL,
}) {
  const [form, setForm] = useState({
    employee: "",
    dates: [], // store multi-selected dates
  });

  // Handle form input changes
  const handleEmployeeChange = (e) => {
    setForm({ ...form, employee: e.target.value });
  };

  const handleSubmit = (e) => {
  e.preventDefault(); // ✅ stop form reload

  if (!form.employee || form.dates.length === 0) return;

  // Sort dates and get first & last
  const sorted = [...form.dates].sort((a, b) => new Date(a) - new Date(b));
  const from = sorted[0].format("YYYY-MM-DD");
  const to = sorted[sorted.length - 1].format("YYYY-MM-DD");

  // Call addLeave function with object
  addLeave({
    employeeId: form.employee,
    from,
    to,
    days: sorted.length,
  });

  // Reset form
  setForm({ employee: "", dates: [] });
};

  // Format date for table display
  const formatDate = (dateStr) => {
    if (!dateStr) return "";
    const d = new Date(dateStr);
    const day = String(d.getDate()).padStart(2, "0");
    const month = String(d.getMonth() + 1).padStart(2, "0");
    const year = d.getFullYear();
    return `${day}-${month}-${year}`;
  };

  return (
    <div>
      {/* Add Leave */}
      <div className="card mb-4 p-4 border rounded shadow">
        <h3 className="text-lg font-bold mb-2">Καταχώρηση Άδειας</h3>
        <form onSubmit={handleSubmit} className="flex flex-col gap-2">
          <select
            name="employee"
            required
            className="border p-2 rounded"
            value={form.employee}
            onChange={handleEmployeeChange}
          >
            <option value="">-- Επέλεξε υπάλληλο --</option>
            {employees.map((e) => (
              <option key={e.id} value={e.id}>
                {e.name}
              </option>
            ))}
          </select>

          {/* Multi-date picker */}
          <DatePicker
            multiple
            value={form.dates}
            onChange={(dates) => setForm({ ...form, dates })}
            format="YYYY-MM-DD"
            className="border p-2 rounded"
          />

          {form.dates.length > 0 && (
            <small className="text-gray-600">
              Επιλεγμένες ημέρες: {form.dates.length}
            </small>
          )}

          <button
            type="submit"
            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
          >
            Καταχώρηση
          </button>
        </form>
      </div>

      {/* Year Filter & Export */}
      <div className="card mb-4 p-4 border rounded shadow">
        <h3 className="text-lg font-bold mb-2">Φίλτρο Έτους</h3>
        <div className="flex items-center gap-2">
          <label>Επιλογή Έτους:</label>
          <input
            type="number"
            value={year}
            onChange={(e) => setYear(e.target.value)}
            className="border p-2 rounded"
          />

          <button
            onClick={doExport}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Export σε Excel
          </button>

          <button
            onClick={() =>
              window.open(`${API_URL}/api/export-summary?year=${year}`, "_blank")
            }
            className="bg-purple-500 text-white px-4 py-2 rounded hover:bg-purple-600"
          >
            Export Σύνοψη
          </button>
        </div>
      </div>

      {/* Employees Table */}
      <div className="card mb-4 p-4 border rounded shadow">
        <h3 className="text-lg font-bold mb-2">Εργαζόμενοι</h3>
        <table className="table-auto border-collapse border w-full">
          <thead>
            <tr>
              <th className="border p-2">Όνομα</th>
              <th className="border p-2">Δικαιούμενες</th>
              <th className="border p-2">Πήρε</th>
              <th className="border p-2">Υπόλοιπο</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((e) => (
              <tr key={e.id}>
                <td className="border p-2">{e.name}</td>
                <td className="border p-2">{e.quota == null ? "-" : e.quota}</td>
                <td className="border p-2">{e.totalTaken}</td>
                <td className="border p-2">{e.remaining == null ? "-" : e.remaining}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Leaves Table */}
      <div className="card mb-4 p-4 border rounded shadow">
        <h3 className="text-lg font-bold mb-2">Καταχωρήσεις Άδειας ({year})</h3>
        <table className="table-auto border-collapse border w-full">
          <thead>
            <tr>
              <th className="border p-2">Εργαζόμενος</th>
              <th className="border p-2">From</th>
              <th className="border p-2">To</th>
              <th className="border p-2">Ημέρες</th>
              <th className="border p-2">Ενέργειες</th>
            </tr>
          </thead>
          <tbody>
            {leaves.map((l) => {
              const emp = employees.find((e) => e.id === l.employeeId);
              return (
                <tr key={l.id}>
                  <td className="border p-2">{emp ? emp.name : l.employeeId}</td>
                  <td className="border p-2">{formatDate(l.from)}</td>
                  <td className="border p-2">{formatDate(l.to)}</td>
                  <td className="border p-2">{l.days}</td>
                  <td className="border p-2 flex gap-2">
                    <button
                      onClick={() => handleEditLeave(l)}
                      style={{ background: "transparent", border: "none", cursor: "pointer", fontSize: "16px" }}
                    >
                      ✏️
                    </button>
                    <button
                      onClick={() => handleDeleteLeave(l.id)}
                      style={{ background: "transparent", border: "none", cursor: "pointer", fontSize: "16px" }}
                    >
                      🗑️
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
}
