import React, { useState } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

export default function EmployeeList({
  employees,
  leaves,
  year,
  setYear,
  addLeave,
  handleEditLeave,
  handleDeleteLeave,
  doExport,
  API_URL,
}) {
  const [employeeId, setEmployeeId] = useState("");
  const [selectedDates, setSelectedDates] = useState([]);

  const handleDateClick = (date) => {
    const iso = date.toISOString().split("T")[0];

    if (selectedDates.includes(iso)) {
      setSelectedDates(selectedDates.filter((d) => d !== iso));
    } else {
      setSelectedDates([...selectedDates, iso].sort());
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!employeeId || selectedDates.length === 0) return;

    const from = selectedDates[0];
    const to = selectedDates[selectedDates.length - 1];
    const days = selectedDates.length;

    addLeave({ employeeId, from, to, days, dates: selectedDates });

    setSelectedDates([]);
    setEmployeeId("");
  };

  return (
    <div>
      {/* Add Leave */}
      <div className="card mb-4 p-4 border rounded shadow">
        <h3 className="text-lg font-bold mb-2">Καταχώρηση Άδειας</h3>
        <form onSubmit={handleSubmit} className="flex flex-col gap-2">
          <select
            value={employeeId}
            onChange={(e) => setEmployeeId(e.target.value)}
            required
            className="border p-2 rounded"
          >
            <option value="">-- Επέλεξε υπάλληλο --</option>
            {employees.map((e) => (
              <option key={e.id} value={e.id}>
                {e.name}
              </option>
            ))}
          </select>

          {/* React Calendar */}
          <Calendar
            onClickDay={handleDateClick}
            tileClassName={({ date }) => {
              const iso = date.toISOString().split("T")[0];
              return selectedDates.includes(iso)
                ? "bg-green-500 text-white rounded"
                : null;
            }}
          />

          <div className="mt-2">
            Επιλεγμένες μέρες: {selectedDates.length} <br />
            Από: {selectedDates[0] || "-"} Έως:{" "}
            {selectedDates[selectedDates.length - 1] || "-"}
          </div>

          <button
            type="submit"
            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
          >
            Καταχώρηση
          </button>
        </form>
      </div>

      {/* Year Filter & Export */}
      <div className="card mb-4 p-4 border rounded shadow">
        <h3 className="text-lg font-bold mb-2">Φίλτρο Έτους</h3>
        <div className="flex items-center gap-2">
          <label>Επιλογή Έτους:</label>
          <input
            type="number"
            value={year}
            onChange={(e) => setYear(e.target.value)}
            className="border p-2 rounded"
          />
          <button
            onClick={doExport}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Export σε Excel
          </button>
          <button
            onClick={() =>
              window.open(`${API_URL}/api/export-summary?year=${year}`, "_blank")
            }
            className="bg-purple-500 text-white px-4 py-2 rounded hover:bg-purple-600"
          >
            Export Σύνοψη
          </button>
        </div>
      </div>

      {/* Employees Table */}
      <div className="card mb-4 p-4 border rounded shadow">
        <h3 className="text-lg font-bold mb-2">Εργαζόμενοι</h3>
        <table className="table-auto border-collapse border w-full">
          <thead>
            <tr>
              <th className="border p-2">Όνομα</th>
              <th className="border p-2">Δικαιούμενες</th>
              <th className="border p-2">Πήρε</th>
              <th className="border p-2">Υπόλοιπο</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((e) => (
              <tr key={e.id}>
                <td className="border p-2">{e.name}</td>
                <td className="border p-2">{e.quota ?? "-"}</td>
                <td className="border p-2">{e.totalTaken}</td>
                <td className="border p-2">{e.remaining ?? "-"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Leaves Table */}
      <div className="card mb-4 p-4 border rounded shadow">
        <h3 className="text-lg font-bold mb-2">Καταχωρήσεις Άδειας ({year})</h3>
        <table className="table-auto border-collapse border w-full">
          <thead>
            <tr>
              <th className="border p-2">Εργαζόμενος</th>
              <th className="border p-2">From</th>
              <th className="border p-2">To</th>
              <th className="border p-2">Ημέρες</th>
              <th className="border p-2">Ενέργειες</th>
            </tr>
          </thead>
          <tbody>
            {leaves.map((l) => {
              const emp = employees.find((e) => e.id === l.employeeId);
              return (
                <tr key={l.id}>
                  <td className="border p-2">{emp ? emp.name : l.employeeId}</td>
                  <td className="border p-2">{l.from}</td>
                  <td className="border p-2">{l.to}</td>
                  <td className="border p-2">{l.days}</td>
                  <td className="border p-2 flex gap-2">
                    <button
                      onClick={() => handleEditLeave(l)}
                      style={{ background: "transparent", border: "none", cursor: "pointer" }}
                    >
                      ✏️
                    </button>
                    <button
                      onClick={() => handleDeleteLeave(l.id)}
                      style={{ background: "transparent", border: "none", cursor: "pointer" }}
                    >
                      🗑️
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
}
