import React, { useState, useEffect } from "react";

const API_URL = "http://0.0.0.0:7020"; // matches App.jsx

export default function AddEmployee({ loadData }) {
  const [year, setYear] = useState(new Date().getFullYear());
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch employees directly from backend
  const fetchEmployees = async () => {
    setLoading(true);
    try {
      const res = await fetch(`${API_URL}/api/employees?year=${year}`);
      const data = await res.json();
      setEmployees(data || []);
    } catch (err) {
      console.error("Error loading employees:", err);
      setEmployees([]);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchEmployees();
  }, [year]);

  // Add new employee
  const handleSubmit = async (e) => {
    e.preventDefault();
    const form = e.target;
    const name = form.name.value.trim();
    const quota = form.quota.value ? Number(form.quota.value) : null;

    if (!name) return alert("Συμπλήρωσε όλα τα απαιτούμενα πεδία!");

    try {
      await fetch(`${API_URL}/api/add-employee`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          id: Date.now().toString(),
          name,
          year,
          days: quota,
        }),
      });
      form.reset();
      if (loadData) loadData(); // refresh parent data
      await fetchEmployees(); // refresh this page’s list
      alert("Ο εργαζόμενος προστέθηκε!");
    } catch (err) {
      console.error("Add employee error:", err);
      alert("Σφάλμα κατά την προσθήκη του εργαζόμενου!");
    }
  };

  // Delete employee
  const handleDelete = async (id, name) => {
    if (!window.confirm(`Είστε σίγουρος ότι θέλετε να διαγράψετε τον εργαζόμενο "${name}" ;`)) return;

    try {
      await fetch(`${API_URL}/api/delete-employee/${id}`, { method: "DELETE" });
      await fetchEmployees();
      if (loadData) loadData();
    } catch (err) {
      console.error("Delete employee error:", err);
      alert("Σφάλμα κατά τη διαγραφή του εργαζόμενου!");
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-xl font-bold mb-4">Προσθήκη Νέου Εργαζόμενου</h2>

      <form onSubmit={handleSubmit} className="flex flex-col gap-3 max-w-md mb-6">
        <input
          name="name"
          placeholder="Όνομα εργαζόμενου"
          required
          className="border p-2 rounded"
        />
        <input
          name="quota"
          type="number"
          placeholder="Σύνολο δικαιούμενων ημερών (προαιρετικό)"
          className="border p-2 rounded"
        />
        <input
          type="number"
          value={year}
          onChange={(e) => setYear(Number(e.target.value))}
          className="border p-2 rounded"
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Προσθήκη
        </button>
      </form>

      <h3 className="text-lg font-bold mb-2">Λίστα Εργαζομένων</h3>
      {loading ? (
        <p>Φόρτωση...</p>
      ) : employees.length === 0 ? (
        <p>Δεν υπάρχουν εργαζόμενοι για το έτος {year}.</p>
      ) : (
        <table className="table-auto border-collapse border w-full">
          <thead>
            <tr>
              <th className="border p-2">Όνομα</th>
              <th className="border p-2">Δικαιούμενες Ημέρες</th>
              <th className="border p-2">Ενέργειες</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((emp) => {
              const quotaObj = emp.quotas?.find((q) => q.year === year);
              const quota = quotaObj ? quotaObj.days : "-";
              return (
                <tr key={emp.id}>
                  <td className="border p-2">{emp.name}</td>
                  <td className="border p-2">{quota}</td>
                  <td className="border p-2">
                    <button
                      onClick={() => handleDelete(emp.id, emp.name)}
                      className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600"
                    >
                      Διαγραφή
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      )}
    </div>
  );
}
