import React from "react";

export default function EmployeeList({
  employees,
  leaves,
  year,
  setYear,
  addLeave,
  loadData,
  doExport,
  handleEditLeave,
  handleDeleteLeave,
  API_URL, // ✅ παίρνουμε το API_URL από τα props
}) {
  // Format date as DD-MM-YYYY
  const formatDate = (dateStr) => {
    if (!dateStr) return "";
    const d = new Date(dateStr);
    const day = String(d.getDate()).padStart(2, "0");
    const month = String(d.getMonth() + 1).padStart(2, "0");
    const year = d.getFullYear();
    return `${day}-${month}-${year}`;
  };

  return (
    <div>
      {/* Add Leave */}
      <div className="card mb-4 p-4 border rounded shadow">
        <h3 className="text-lg font-bold mb-2">Καταχώρηση Άδειας</h3>
        <form onSubmit={addLeave} className="flex flex-col gap-2">
          <select name="employee" required className="border p-2 rounded">
            <option value="">-- Επέλεξε υπάλληλο --</option>
            {employees.map((e) => (
              <option key={e.id} value={e.id}>
                {e.name}
              </option>
            ))}
          </select>
          <input type="date" name="from" required className="border p-2 rounded" />
          <input type="date" name="to" required className="border p-2 rounded" />
          <button
            type="submit"
            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
          >
            Καταχώρηση
          </button>
        </form>
      </div>

      {/* Year Filter & Export */}
      <div className="card mb-4 p-4 border rounded shadow">
        <h3 className="text-lg font-bold mb-2">Φίλτρο Έτους</h3>
        <div className="flex items-center gap-2">
          <label>Επιλογή Έτους:</label>
          <input
            type="number"
            value={year}
            onChange={(e) => setYear(e.target.value)}
            className="border p-2 rounded"
          />

          <button
            onClick={doExport}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Export σε Excel
          </button>

          <button
            onClick={() =>
              window.open(`${API_URL}/api/export-summary?year=${year}`, "_blank")
            }
            className="bg-purple-500 text-white px-4 py-2 rounded hover:bg-purple-600"
          >
            Export Σύνοψη
          </button>
        </div>
      </div>

      {/* Employees Table */}
      <div className="card mb-4 p-4 border rounded shadow">
        <h3 className="text-lg font-bold mb-2">Εργαζόμενοι</h3>
        <table className="table-auto border-collapse border w-full">
          <thead>
            <tr>
              <th className="border p-2">Όνομα</th>
              <th className="border p-2">Δικαιούμενες</th>
              <th className="border p-2">Πήρε</th>
              <th className="border p-2">Υπόλοιπο</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((e) => (
              <tr key={e.id}>
                <td className="border p-2">{e.name}</td>
                <td className="border p-2">
                  {e.quota == null ? "-" : e.quota}
                </td>
                <td className="border p-2">{e.totalTaken}</td>
                <td className="border p-2">
                  {e.remaining == null ? "-" : e.remaining}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Leaves Table */}
      <div className="card mb-4 p-4 border rounded shadow">
        <h3 className="text-lg font-bold mb-2">Καταχωρήσεις Άδειας ({year})</h3>
        <table className="table-auto border-collapse border w-full">
          <thead>
            <tr>
              <th className="border p-2">Εργαζόμενος</th>
              <th className="border p-2">From</th>
              <th className="border p-2">To</th>
              <th className="border p-2">Ημέρες</th>
              <th className="border p-2">Ενέργειες</th>
            </tr>
          </thead>
          <tbody>
            {leaves.map((l) => {
              const emp = employees.find((e) => e.id === l.employeeId);
              return (
                <tr key={l.id}>
                  <td className="border p-2">{emp ? emp.name : l.employeeId}</td>
                  <td className="border p-2">{l.from}</td>
                  <td className="border p-2">{l.to}</td>
                  <td className="border p-2">{l.days}</td>
                  <td className="border p-2 flex gap-2">
                    {/* Edit button */}
                    <button
                      onClick={() => handleEditLeave(l)}
                      style={{
                        background: "transparent",
                        border: "none",
                        cursor: "pointer",
                        fontSize: "16px",
                      }}
                    >
                      ✏️
                    </button>
                    {/* Delete button */}
                    <button
                      onClick={() => handleDeleteLeave(l.id)}
                      style={{
                        background: "transparent",
                        border: "none",
                        cursor: "pointer",
                        fontSize: "16px",
                      }}
                    >
                      🗑️
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
}
