import React, { useState } from "react";
const API_URL = "http://0.0.0.0:7020";

export default function AddEmployee({ loadData }) {
  const [year, setYear] = useState(new Date().getFullYear());

  const handleSubmit = async (e) => {
    e.preventDefault();
    const form = e.target;
    const name = form.name.value.trim();
    const quota = form.quota.value ? Number(form.quota.value) : null;

    if (!name || !year) return alert("Συμπλήρωσε όλα τα απαιτούμενα πεδία!");

    await fetch(`${API_URL}/api/add-employee`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        id: Date.now().toString(),
        name,
        year,
        days: quota
      }),
    });

    form.reset();
    if (loadData) loadData();
    alert("Ο εργαζόμενος προστέθηκε!");
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-xl font-bold mb-4">Προσθήκη Νέου Εργαζόμενου</h2>
      <form onSubmit={handleSubmit} className="flex flex-col gap-3 max-w-md">
        <input name="name" placeholder="Όνομα εργαζόμενου" required className="border p-2 rounded" />
        <input name="quota" type="number" placeholder="Σύνολο δικαιούμενων ημερών (προαιρετικό)" className="border p-2 rounded" />
        <input type="number" value={year} onChange={e => setYear(Number(e.target.value))} className="border p-2 rounded" />
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Προσθήκη</button>
      </form>
    </div>
  );
}
